import React, { useState, useEffect } from 'react'
import '../Css/NewsFeed.css'
import WhoToFollow from './WhoToFollow'
import CreatePost from './CreatePost'
import Tweets from './Tweets'
import SideBar from './SideBar'
import axios from 'axios'
import { token } from '../Config/Token'
import shortid from 'shortid'

function NewsFeed() {

    const [feeds, setfeeds] = useState([])

    const proxyurl = "https://cors-anywhere.herokuapp.com/";
    const url = 'https://api.twitter.com/1.1/search/tweets.json?q=%23Dogs&result_type=recent&count=20'

    useEffect(() => {
        const getData = async () => {
            const res = await axios.get(proxyurl + url, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            })
            setfeeds(res.data.statuses)
        };
        getData()
    }, [])

    // const { text, user, entities } = feeds;

    // const { media } = entities



    const filterFeed = feeds.filter(medias => medias.entities.media['0'].media_url_https.length > 0)

    console.log(filterFeed)




    return (
        <section className="feeds_page">
            <SideBar />
            <div className="feeds-content">
                <CreatePost />
                {feeds.length === 0 ? 'loading..' : feeds.map(feed => <Tweets key={shortid.generate()} feed={feed} />)}
                <WhoToFollow />
            </div>
        </section >
    )
}

export default NewsFeed
